// ignore_for_file: library_private_types_in_public_api, use_build_context_synchronously, unnecessary_null_comparison

import 'package:citta_admin_panel/inner_screen/edit_fashion_product_screen.dart';
import 'package:cloud_firestore/cloud_firestore.dart';
import 'package:fancy_shimmer_image/fancy_shimmer_image.dart';
import 'package:flutter/material.dart';

import '../services/utils.dart';
import 'text_widget.dart';

class FashionWidget extends StatefulWidget {
  const FashionWidget({
    super.key,
    required this.title,
    required this.price,
    required this.image,
    required this.fashionProductID,
  });
  final String title;
  final String price;
  final String image;
  final String fashionProductID;
  @override
  _FashionWidgetState createState() => _FashionWidgetState();
}

class _FashionWidgetState extends State<FashionWidget> {
  String? imageUrl;
  bool isLoading = false;
  @override
  void initState() {
    getProductData();
    super.initState();
  }

  void getProductData() async {
    setState(() {
      isLoading = true;
    });
    try {
      final DocumentSnapshot productsDoc = await FirebaseFirestore.instance
          .collection('fashion')
          .doc(widget.fashionProductID)
          .get();
      if (productsDoc == null) {
        return;
      } else {
        imageUrl = productsDoc.get('imageUrl');
      }
    } catch (error) {
      setState(() {
        isLoading = false;
      });

      errorDialog(subtitle: error.toString(), context: context);
    } finally {
      setState(() {
        isLoading = false;
      });
    }
  }

  @override
  Widget build(BuildContext context) {
    Size size = Utils(context).getScreenSize;

    final color = Utils(context).color;
    return Padding(
      padding: const EdgeInsets.all(8.0),
      child: Material(
        color: Theme.of(context).cardColor.withOpacity(0.6),
        child: InkWell(
          borderRadius: BorderRadius.circular(12),
          onTap: () {},
          child: Padding(
            padding: const EdgeInsets.all(8.0),
            child: Column(
              mainAxisAlignment: MainAxisAlignment.start,
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: [
                    Flexible(
                      flex: 3,
                      child: imageUrl == null
                          ? Image.network(
                              "data:image/png;base64,",
                              scale: 5.0,
                              fit: BoxFit.fill,
                              // width: screenWidth * 0.12,
                              height: size.width * 0.12,
                            )
                          : FancyShimmerImage(
                              imageUrl: widget.image,
                              boxFit: BoxFit.fill,
                              height: size.width * 0.12,
                              width: 150,
                            ),
                    ),
                    const Spacer(),
                    PopupMenuButton(
                        itemBuilder: (context) => [
                              PopupMenuItem(
                                onTap: () {
                                  Navigator.push(
                                    context,
                                    MaterialPageRoute(
                                      builder: (c) => EditFashionProductScreen(
                                          id: widget.fashionProductID),
                                    ),
                                  );
                                },
                                value: 1,
                                child: const Text('Edit'),
                              ),
                              PopupMenuItem(
                                onTap: () {},
                                value: 2,
                                child: const Text(
                                  'Delete',
                                  style: TextStyle(color: Colors.red),
                                ),
                              ),
                            ])
                  ],
                ),
                const SizedBox(
                  height: 2,
                ),
                Row(
                  children: [
                    TextWidget(
                      text: widget.price,
                      color: color,
                      textSize: 18,
                    ),
                    const SizedBox(
                      width: 7,
                    ),
                    Visibility(
                        visible: false,
                        child: Text(
                          '\$18.89',
                          style: TextStyle(
                              decoration: TextDecoration.lineThrough,
                              color: color),
                        )),
                  ],
                ),
                const SizedBox(
                  height: 2,
                ),
                TextWidget(
                  text: widget.title,
                  color: color,
                  textSize: 18,
                  isTitle: true,
                ),
              ],
            ),
          ),
        ),
      ),
    );
  }

  static Future<void> errorDialog({
    required String subtitle,
    required BuildContext context,
  }) async {
    await showDialog(
        context: context,
        builder: (context) {
          return AlertDialog(
            title: Row(
              children: [
                Image.asset(
                  "assets/images/warning-sign.png",
                  height: 20,
                  width: 20,
                  fit: BoxFit.cover,
                ),
                const SizedBox(
                  width: 8,
                ),
                const Text("An Error occured"),
              ],
            ),
            content: Text(subtitle),
            actions: [
              TextButton(
                onPressed: () {
                  Navigator.pop(context);
                },
                child: TextWidget(
                  text: "ok",
                  color: Colors.cyan,
                  textSize: 18,
                ),
              ),
            ],
          );
        });
  }
}
